var link=document.querySelector(".info__contacts .info__btn"),popup=document.querySelector(".write-us-form"),close=popup.querySelector(".close-btn"),email=popup.querySelector("[name=email]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("write-us-form--active"),email.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("write-us-form--active")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("write-us-form--active")&&popup.classList.remove("write-us-form--active")});